import React, { useState } from 'react';

function App() {
  const [isAbout, setIsAbout] = useState(false);

  function handleClick() {
    setIsAbout(true);
  }

  return (
    <div>
      {isAbout ? (
        <About />
      ) : (
        <Home onClick={handleClick} />
      )}
    </div>
  );
}

function Home({ onClick }) {
  return (
    <div>
      <h2>Home</h2>
      <button onClick={onClick}>Go to About</button>
    </div>
  );
}

function About() {
  return <h2>About</h2>;
}

export default App;
